let FormContentsComponent = class FormContentsComponent {
    constructor(formConfigService, modalService, cd, contentService) {
        this.formConfigService = formConfigService;
        this.modalService = modalService;
        this.cd = cd;
        this.contentService = contentService;
        this.options = {
            size: 'lg',
            backdrop: 'static',
            keyboard: false,
            centered: true
        };
    }
    ngOnInit() {
        this.showOptions = false;
        this.formConfigService
            .getConfig()
            .subscribe(data => {
            this.config = data;
            this.cd.markForCheck();
        });
    }
    trackByFn(index, item) {
        return index;
    }
    copyHtml(content) {
        let bootstrapHtmlTemplate = new BootstrapHtmlTemplate();
        let code = bootstrapHtmlTemplate.get(content);
        this.copyToClipboard(code);
    }
    copyToClipboard(text) {
        if (window.clipboardData && window.clipboardData.setData) {
            // IE specific code path to prevent textarea being shown while dialog is visible.
            return window.clipboardData.setData("Text", text);
        }
        else if (document.queryCommandSupported && document.queryCommandSupported("copy")) {
            var textarea = document.createElement("textarea");
            textarea.textContent = text;
            textarea.style.position = "fixed"; // Prevent scrolling to bottom of page in MS Edge.
            document.body.appendChild(textarea);
            textarea.select();
            try {
                return document.execCommand("copy"); // Security exception may be thrown by some browsers.
            }
            catch (ex) {
                console.warn("Copy to clipboard failed.", ex);
                return false;
            }
            finally {
                document.body.removeChild(textarea);
            }
        }
    }
    sendDataToModal(contents, index) {
        const modal = this.modalService.open(FormConfigComponent, this.options);
        modal.componentInstance.content_id = contents[index].id;
        modal.componentInstance.content = contents[index];
        modal.componentInstance.emitData.subscribe($e => {
            contents[index] = $e;
            this.cd.markForCheck();
            /*this.contentService.updateContent($e).subscribe(result => {
                console.log(result);
                if(result.success){
                    contents[index] = result.data;
                    this.cd.markForCheck();
                }
            });*/
        });
    }
    deleteContent(contentIndex, content = { id: null }) {
        if (content.id != null) {
            this.contentService
                .deleteContent(content.id)
                .subscribe(result => {
                if (result.success) {
                    this.column.contents.forEach((cV, index) => {
                        if (cV.id == content.id) {
                            this.column.contents.splice(index, 1);
                        }
                    });
                    this.cd.markForCheck();
                }
            });
        }
        else {
            this.column.contents.splice(contentIndex, 1);
        }
    }
